import React, {Component} from 'react';

class Counter extends Component{
	state = {
		count: 0,
		tags: ['tag1', 'tag2', 'tag3'],
	};

	styles = {
		fontSize:50,
		fontWeight:'bold',
	};

	render(){
		return (
			<React.Fragment>
				<span style={this.styles} className={this.getBadgeClasses()}>{this.formatCount()}</span>
				<br/>
				<button className="butt">Increment</button>
				<br />
				<ul className="list">
					{this.state.tags.map(tag => <li key={tag}>{tag}</li>)}
				</ul>
			</React.Fragment>
		);
	}

	getBadgeClasses(){
		let classes = 'answer-';
		classes += (this.state.count === 0) ? 'bad' : 'good';
		return classes;
	}

	formatCount(){
		const {count} = this.state;
		return count === 0 ? 'Zero' : count;
	}
}

class Counter2 extends Component{
	state = {
		count: 0,
		tags: ['tag1', 'tag2', 'tag3'],
	};

	render(){
		return (
			<React.Fragment>
				<ul className="list">
					{this.state.tags.map(tag => <li key={tag}>{tag}</li>)}
				</ul>
			</React.Fragment>
		);
	}
}

export default Counter2;
