import React, {Component} from 'react';

class Counter extends Component{
	state = {
		value: this.props.value,
	};

	styles = {
		fontSize:50,
		fontWeight:'bold',
	};

/*	binds event handlers

	constructor(){
		super();
		this.handleIncrement = this.handleIncrement.bind(this);
	}*/

	eventhandleIncrement=(/*product*/)=>{
		this.setState({value:this.state.value + 1});
	};

	eventhandleDecrement=()=>{
		this.setState({value:this.state.value - 1});
	};

	render(){
		//everything has props used to identify the properties of a component
		//console.log('props', this.props);
		
		return (
			<div className="counter-element">

				<h4>{this.props.id}</h4>		{/* or {this.props.children}*/}
				<span style={this.styles} className={this.getBadgeClasses()}>{this.formatCount()}</span>
				<br/>
				<button onClick={()=>this.eventhandleIncrement(/*product*/)} className="butt">Increment</button>
				<br />
				<button onClick={this.eventhandleDecrement} className="butt">Decrement</button>
				<br />
			</div>
		);
	}

	getBadgeClasses(){
		let classes = 'answer-';
		classes += (this.state.value === 0) ? 'bad' : 'good';
		return classes;
	}

	formatCount(){
		const {value} = this.state;
		return value === 0 ? 'Zero' : value;
	}
}

















class Counter2 extends Component{
	state = {
		value: 0,
		tags: [/*'tag1', 'tag2', 'tag3'*/],
	};

	renderTags(){
		if(this.state.tags.length == 0){
			return <p>There are no tags</p>;
		} else{
			return 	<ul className="list">
						{this.state.tags.map(tag => <li key={tag}>{tag}</li>)}
					</ul>
		}
	}

	render(){
		return (
			<React.Fragment>
				{this.state.tags.length === 0 && <p>Please create a new tag</p>}
				{this.renderTags()}
			</React.Fragment>
		);
	}
}

export default Counter;
